import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";
import NavLayout from "../components/Recruitment/NavLayout";
import { useGlobalContext } from "../context/context";

import axios from "axios";
import { useNavigate } from "react-router-dom";

import CreateJob from "../components/Recruitment/CreateJob";
import JobCard from "../components/Recruitment/JobCard";
const Recuirtment = () => {
  const { loading, jobData, fetchJobData } = useGlobalContext();

  useEffect(() => {
    fetchJobData();
  }, []);

  return (
    <Layout>
      <NavLayout select="jobs">
        <div className="py-4">
          {loading ? (
            <h1>Loading..</h1>
          ) : (
            <div className="">
              <div className="flex justify-end items-center ">
                <label
                  htmlFor="my-modal-3"
                  className="btn mr-5 mb-6 border-none bg-sky-500 text-white "
                >
                  Create
                </label>
                <CreateJob />
              </div>
              {jobData.length === 0 ? (
                <h1 className="text-md font-semibold text-gray-700 mx-8">
                  No data available
                </h1>
              ) : (
                <div className="flex flex-col justify-center mx-5 sm:mx-0 xs:mx-0 gap-5">
                  {jobData?.map((job) => (
                    <JobCard key={job._id} data={job} />
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      </NavLayout>
    </Layout>
  );
};

export default Recuirtment;
