import React from "react";
// import { useForm } from "react-hook-form";
import { Layout } from "../../components";
import { useEffect } from "react";

const Changepswd = () => {
  
  return (
    <Layout>
      {/* <Toaster
        toastOptions={{
          className: "",
          style: {
            padding: "16px",
          },
        }}
      /> */}
      <div className=" flex items-center justify-center h-screen">
        <div className="relative items-stretch flex w-[60%] xs:w-[80%] sm:w-[98%] md:w-[85%] lg:w-[60%] border-[1px] border-gray-200 shadow-lg bg-white justify-center overflow-hidden">
          <div className="flex-1 xs:hidden sm:hidden md:block max-sm:block lg:block">
            {/* <Lottie options={defaultOptions} height={390} /> */}
          </div>
          <div className="w-full flex-1 p-6 bg-gradient-to-r from-sky-300 to-sky-50 px-8  lg:max-w-xl">
            <h1 className="text-3xl xs:text-xl sm:text-2xl font-semibold text-center text-black  underline">
              Change Password
            </h1>
            <form className="mt-8">
              <div className="my-5 w-full">
                <label
                  for="password"
                  className="block text-sm font-semibold text-gray-800"
                >
                  Password
                </label>
                <input
                  name="password"
                  type="password"
                 
                  className='block w-full px-4 py-2 mt-2 bg-white border rounded-md focus:outline-none focus:ring focus:ring-opacity-40'
                />
                {/* <p className="text-red-500">{errors.password?.message}</p> */}
              </div>
              <div className="form-group">
                <label
                  for="confirmPassword"
                  className="block text-sm font-semibold text-gray-800"
                >
                  Cofirm Password
                </label>
                <input
                  name="confirmPassword"
                  type="password"
                  className='block w-full px-4 py-2 mt-2 bg-white border rounded-md focus:outline-none focus:ring focus:ring-opacity-40'
                />
                <p className="text-red-500">
                  {/* {errors.confirmPassword?.message} */}
                </p>
              </div>
              <button
                type="primary"
                className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-sky-600 hover:bg-sky-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 mt-10"
              >
                Submit
              </button>
            </form>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default Changepswd;
